<body>
<div class="col-lg-10 cus-content">
	<div class="panel panel-default">
		<!-- <div class="panel panel-default"> -->

			

<div class="panel-body">
<form enctype="multipart/form-data" action="" method="post"  id="form_edit">
    <table class="table table-bordered table-hover definewidth m10">
        <tr>
            <td width="10%" class="tableleft">菜品名称</td>
            <td><input type="text" name="goods_name"  value="<?php echo ($goodsData["goods_name"]); ?>" autocomplete="off" required/></td>
        </tr>
        <tr>
            <td width="10%" class="tableleft">所属分类</td>
            <td>
            <select name="cat_id" required>
	            <?php if(is_array($cat)): foreach($cat as $key=>$vo): if($vo['id'] == $goodsData['cat_id']) $select = 'selected="selected"'; else $select = ''; ?>
	            <option value="<?php echo ($vo["id"]); ?>"  <?php echo ($select); ?> ><?php echo ($vo["name"]); ?></option><?php endforeach; endif; ?>
            </select>
            </td>
        </tr>
        <tr>
            <td width="10%" class="tableleft">菜品价格</td>
            <td><input type="text" name="goods_price" value="<?php echo ($goodsData["goods_price"]); ?>"  autocomplete="off" required/></td>
        </tr>
        <tr>
            <td width="10%" class="tableleft">菜品图片</td>
            <td>
	            <?php showImage($goodsData['goods_sm_pic'], 60,60); ?>
	            <input type="file" name="goods_pic" />
            </td>
        </tr>
        <tr>
        <td class="tableleft">是否推荐</td>
            <td >
            	<?php  if($goodsData['is_recommend'] == 1){ $is_checked = 'checked="checked"'; $no_checked = ''; }else{ $is_checked = ''; $no_checked = 'checked="checked"'; } ?>
            	<input type="radio" name="is_recommend" value="1"  <?php echo ($is_checked); ?> />是
            	<input type="radio" name="is_recommend" value="0"  <?php echo ($no_checked); ?> style="margin-left: 20px;" />否
            </td>
        </tr>
        <input type="hidden" name="id" value="<?php echo ($goodsData["id"]); ?>">
        <tr>
            <td class="tableleft" id="tips" style="color:red;text-align:center;"></td>
            <td>
                <button type="submit" class="btn btn-primary" type="button">保存</button>              &nbsp;&nbsp;
                <button type="button" class="btn btn-success" name="backid" id="backid">返回</button>
            </td>
        </tr>
    </table>
</form>
