$('#width').click(function(){
	var width = $(this).width(); 
	alert(width);
});

function exportExcel(th){
    var fromDate = $('#from-date').val();
    var toDate = $('#to-date').val();
    window.location.href="<?php echo U('Statistics/exportExcel');?>/?from-date="+fromDate +"&to-date="+toDate;
} 

